import React, { useRef } from 'react';
import { Formik, Form, Field, ErrorMessage, useField } from 'formik';

const Radio = ({label, ...props}) => {
    const [field] = useField({ ...props, type: 'radio'});
    return(
        <div>
            <label>
                <input type='radio' {...field} {...props}/>
                {label}
            </label>
        </div>
    );
}

const Button = (props) => {
    let button;
    if(props.estado < 3){
        button = <button type="submit">Siguiente</button>
    }
    else{
        button = <button type="submit">Finalizar</button>
    }
    return(
        <div>{button}</div>
    );
}

const Formularios = (props) => {
    let formulario = '';
    if(props.estado === 0){
        formulario = 
            <Form>
                <Radio name="tipo" value="estudiante" label="Estudiante" />
                <br/>
                <Radio name="tipo" value="tutor" label="Tutor" />
                <br/>
                <Button name="boton" estado={props.estado}/>
            </Form>
    }
    else if(props.estado === 1){
        formulario = 
            <Form>
                <Radio name="tipo" value="tutor" label="Tutor" />
                <br/>
                <Button name="tipo" estado={props.estado}/>
            </Form>
    }
    return (
        <div>{formulario}</div>
    );
}

var estado = 0;
// 0 => tutor o estudiante
// 1 => datos personales
// 2 => asignaturas que realiza
// 3 => horario

const SignUp = () => {
    return(
        <div>
            <h1>PÃ¡gina crear cuenta</h1>
                <Formik 
                    initialValues={{tipo:''}}
                    onSubmit={(values => console.log(values))}
                >
                    <Formularios estado={estado}/>
                </Formik>
        </div>
    );
}

export default SignUp
