import React from 'react';
import './Horario.css';

const horas = 16;
const cant_dias = 7;
const dias = ['Lunes', 'Martes', 'Miercoles', 'Jueves', 'Viernes'];

const Bloque = (props) => {
    return(
        <div hora={props.hora} dia={props.dia}>{props.hora}</div>
    );
}

const Horas = (props) => {
    let bloque = [];
    for(let i = 1; i <= horas; i++){
        bloque.push(<Bloque hora={i} dia={props.dia}/>)
    }
    return(
        {bloque}
    );

}

const Dia = () => {
    return(
    <div className='grid-container'>
        <Horas />
    </div>
    );

}

const Horario = () => {
    return(
        <Bloque />
    );
}

export default Horario;





































