import { useEffect, useState } from 'react';
import axios from 'axios'

const APIZoom = axios.create({
  withCredentials: true, 
  credentials: 'include',
  baseURL: `http://localhost:2000/api/zoom`
})

const Reunion = () => {
    const [enlace_reunion, setEnlace] = useState("")
    const [validarEnlace, setValidacion] = useState(false)

    useEffect(() => {
        const generarEnlace = async () => {
        try{
                const mi_token = localStorage.getItem('x-token')
                const response = APIZoom.get("/ZoomMeet", {
                    headers: {
                        'Content-type': 'application/json',
                        'x-token': mi_token
                    }
                })
                if(response.data){
                    if(!validarEnlace){
                        setEnlace(response.data.enlaceZoom)
                        setValidacion(true)
                    }
                }
        }catch(error){
            console.log(error)
        }
        }
        generarEnlace();
    }, [])

    return(
        <>
        {
            enlace_reunion !== "" &&
            <a href={enlace_reunion}>{"Su enlace es: "+enlace_reunion}</a>
        }
        </>


    )

}

export default Reunion
