import { ZoomMtg } from '@zoomus/websdk';
import ZoomMtgEmbedded from "@zoomus/websdk/embedded";
import { useEffect, useState } from 'react';
import axios from 'axios'

ZoomMtg.preLoadWasm();
ZoomMtg.prepareJssdk();

const signatureEndpoint = 'http://localhost:2000'
const apiKey = 'JWT_API_KEY'
const role = 0 // tutor
const leaveUrl = 'http://localhost:9999'
const userName = 'WebSDK'
const userEmail = ''
const passWord = ''

const client = ZoomMtgEmbedded.createClient();
const APIZOOM = axios.create({
  withCredentials: true, 
  credentials: 'include',
  baseURL: `http://localhost:2000/api/zoom`
})

const Reunion = () => {
    const [enlace_reunion, setEnlace] = useState("")
    const [validarEnlace, setValidacion] = useState(false)

    useEffect(() => {
        const generarEnlace = async () => {
        try{
                const mi_token = localStorage.getItem('x-token')
                const response = APIZOOM.get("/ZoomMeet", {
                    headers: {
                        'Content-type': 'application/json',
                        'x-token': mi_token
                    }
                })
                if(response.data){
                    if(!validarEnlace){
                        setEnlace(response.data.enlaceZoom)
                        setValidacion(true)
                    }
                }
        }catch(error){
            console.log(error)
        }
        }
        generarEnlace();
    }, [])

    return(
        <a href={enlace_reunion}>{"Su enlace es: "+enlace_reunion}</a>
    )

}

export default Reunion
