import React, { useRef } from 'react';
import { Formik, Form, Field, ErrorMessage, useField } from 'formik';

const Radio = ({label, ...props}) => {
    const [field] = useField({ ...props, type: 'radio'});
    return(
        <div>
            <label>
                <input type='radio' {...field} {...props}/>
                {label}
            </label>
        </div>
    );
}

const Button = () => {
    let button;
    if(estado < 3){
        button = <button onClick={estado += 1}>Siguiente</button>

    }
    else{
        button = <button onClick={estado += 1}>Finalizar</button>

    }
    return(
        <div>{button}</div>
    );
}

const Formularios = (estado) => {
    let formulario;
    if(estado === 0){
        formulario = 
            <Form>
                <Radio name="radio" value="estudiante" label="Estudiante" />
                <br/>
                <Radio name="radio" value="tutor" label="Tutor" />
                <br/>
                <Button name="boton"/>
            </Form>
    }
    else if(estado === 1){
        formulario = 
            <Form>
                <Radio name="radio" value="tutor" label="Tutor" />
                <br/>
                <Button name="boton"/>
            </Form>
    }
    return (
        <div>{formulario}</div>
    );
}

const SignUp = () => {

    let estado = 0;
    // 0 => tutor o estudiante
    // 1 => datos personales
    // 2 => asignaturas que realiza
    // 3 => horario
    return(
        <div>
            <h1>PÃ¡gina crear cuenta</h1>
                <Formik 
                    initialValues={{estudiante: '', tutor:'',}}
                    onSubmit={values => console.log(values)}
                >
                    <Formularios estado={estado}/>
                </Formik>
        </div>
    );
}

export default SignUp
