import React from 'react';
import './Horario.css';

const horas = [
    '8:00 AM',
    '9:00 AM',
    '10:00 AM',
    '11:00 AM',
    '12:00 PM',
    '13:00 PM',
    '14:00 PM',
    '15:00 PM',
    '16:00 PM',
    '17:00 PM',
    '18:00 PM',
    '19:00 PM',
    '20:00 PM',
    '21:00 PM',
    '22:00 PM',
    '23:00 PM'
] 
const dias = ['Lunes', 'Martes', 'Miercoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];

const Bloque = (props) => {
    return(
        <div hora={props.hora} dia={props.dia}>{props.hora}</div>
    );
}

const Hora = (props) => {
    let bloque = [];
    for(let i = 0; i < horas.length; i++){
        bloque.push(<Bloque hora={horas[i]} dia={props.dia} key={i}/>)
    }
    return(
        <>{bloque}</>
    );

}

const Dia = () => {
    let horario = dias.map((dias) => 
        <div className='grid-dias'>
            <div className='grid-horas'>
            {dias}
                <Hora dia={dias} key={dias}/> 
            </div>
        </div>
        )

    //let hora = [];
    //for(let i = 0; i < horas.length; i++){
        //hora.push(<Bloque hora={horas[i]} dia="" key={i}/>)
    //}
    let hora = horas.map((hora) => 
        <div >
            {hora}
        </div> 
    )

    return(
        <div className='grid-horario'>
            <div className="grid-horas">
                {hora}
            </div>
            {horario}
        </div>
    );
}

const Horario = () => {
    return(
        <Dia />
    );
}

export default Horario;





































