import { useState } from 'react';
import './Dropdown.css'

const Dropdown = (props) => {

    const [mostrar, setMostrar] = useState(false);
    const updateMostrar = () => {
        setMostrar(!mostrar)
    }

    return(
        <div className="dropdown">
            <button type="button" onClick={updateMostrar()}>{props.value}</button>
               {props.component} 
        </div>
    );
}

export default Dropdown;
