import React from 'react';
import { useField } from 'formik';
import asignaturas from './Asignaturas.json'
import CheckBox from '../botones/Checkbox';

// <option> mostrar ramo por grupo
const OpcionAsignatura = (props) => {
    const opcion_asignatura = asignaturas.filter((ramo) => ramo.materia === props.name).map((ramo) => 
        <option key={ramo.id} value={ramo.ramo}>
            {ramo.ramo}
        </option>
    );

    const checkbox_asignatura = asignaturas.filter((ramo) => ramo.materia === props.name).map((ramo) => 
        <CheckBox key={ramo.id} value={ramo.ramo} />
    );
    return(
        <>{opcion_asignatura}</>
    );
}

// <optgroup> mostrar por grupo
const GroupAsignatura = (props) => {
    return(
        <div>
            {
                props.value === "dropdown" ?
                <optgroup key="optgroup" label={props.name}>
                    <OpcionAsignatura name={props.name}/>
                </optgroup>
                :
                    <OpcionAsignatura name={props.name}/>
            }

        </div>
    );
}

// <select>
const SelectAsignaturas = (props) => {
    const [field] = useField(props);
    return(
        <div>
        {
            props.value === "dropdown" ? 
                <select {...field} {...props}>
                    <option key="optgroup" label="Seleccione"></option>
                    <GroupAsignatura name="Matemáticas" value="dropdown"/>
                    <GroupAsignatura name="Física" value="dropdown"/>
                    <GroupAsignatura name="Informática" value="dropdown"/>
                </select>
            :
                <div> 
                    <GroupAsignatura name="Matemáticas" value="checkbox"/>
                    <GroupAsignatura name="Física" value="checkbox"/>
                    <GroupAsignatura name="Informática" value="checkbox"/>
                </div>
        }

        </div>
    );
}

export default SelectAsignaturas;
