import React, { useRef, useState } from 'react';
import { Formik, Form, Field, ErrorMessage, useField } from 'formik';

const Radio = ({label, ...props}) => {
    const [field] = useField({ ...props, type: 'radio'});
    return(
        <div>
            <label>
                <input type='radio' {...field} {...props}/>
                {label}
            </label>
        </div>
    );
}

const Button = (props) => {
    let button = '';
    if(props.estado < 3){
        button = <button type="submit">Siguiente</button>
    }
    else{
        button = <button type="submit">Crear cuenta</button>
    }
    return(
        <div>{button}</div>
    );
}

const FormularioTutor = (props) => {
    return(
        <h1>hola</h1>
    );
}

const FormularioEstudiante = (props) => {
    return(
        <h1>chao</h1>
    );

}

const validaciones = (values) => {
    const errors = {};

    if(!values.tipo){
        errors.tipo = 'Debe asignar un valor'
    }

    return errors;
}

let usuario = {
    tipo: ''
}

const SignUp = () => {
    const [estado, setEstado] = useState(0);
    // updateEstado -> avanza en el formulario
    // estado = 0 -> tutor o estudiante
    // estado = 1 -> datos personales 
    // estado = 2 -> asignaturas
    // estado = 3 -> horario
    const updateEstado = () => {
        setEstado(estado+1);
    }

    const [tipo, setTipo] = useState('');
    const updateTipo = (nuevo_tipo) => {
        setTipo(nuevo_tipo);
    }


    return(
        <div>
            <h1>PÃ¡gina crear cuenta</h1>
            <Formik 
                initialValues={{tipo:'inicial'}}
                validate={validaciones}
                onSubmit={(values => updateTipo(values.tipo))}
            >
                {tipo === 'inicial' &&
                    <Form>
                        <Radio name="tipo" value="estudiante" label="Estudiante" />
                        <br/>
                        <Radio name="tipo" value="tutor" label="Tutor" />
                        <br/>
                        <ErrorMessage name="tipo" />
                        <Button name="boton"/>
                    </Form>
                }
                {tipo === 'tutor' && <FormularioTutor />}
                {tipo === 'estudiante' && <FormularioEstudiante />}
            </Formik>
        </div>
    );
}

export default SignUp
