import React, { useEffect, useState } from 'react';
import '../pages_css/Perfil.css';
import PerfilTutor from './PerfilTutor';
import PerfilEstudiante from './PerfilEstudiante';
import axios from 'axios';
import { AiOutlinePicRight } from 'react-icons/ai';

const api = axios.create({
  withCredentials: true, 
  credentials: 'include',
  baseURL: `http://localhost:2000/api/checkToken`
})

const Perfil = () => {
    const [tutor, setTutor] = useState([])
    const [dataTutor, setdataTutor] = useState(true)
    useEffect(()=>{
        const obtenerTutor = async () => {
            try{
                const mi_token = localStorage.getItem('x-token')
                //console.log(mi_token)
                const tipo_usuario = await api.get('/checkUser',{
                    headers: {
                    'Content-type': 'application/json',
                    'x-token': mi_token
                    }
                });
                if(dataTutor){
                    setTutor(tipo_usuario.data)
                    setdataTutor(false)
                }
            }catch(error){
                console.log(error)
            }
        }
        obtenerTutor();
        console.log(tutor)
    }, [tutor])

    return(
        <div>
            <div>
                <h1>l </h1>
                {}
                <h1>Mi perfil </h1>
            </div>
            {tutor.tipo === "estudiante" ?
            <PerfilEstudiante /> 
            :
            <PerfilTutor />
            }
        </div>
    );
}

export default Perfil;
