import React, { useState } from 'react';
import { Formik, Form, Field, ErrorMessage, useField, FieldArray } from 'formik';
import BotonFormulario from '../../botones/BotonFormulario'

const FormularioAsignaturas = (props) => {
    return(
        <div>
            <label>{props.value}</label>
            <FieldArray name="asignaturas" >
                {(fieldArrayProps) => {
                    const { push, remove, form } = fieldArrayProps;
                    const { values } = form;
                    const { asignaturas } = values;
                    return (
                        <div>
                        {
                            asignaturas.map((asignatura, i) => (
                                <div key={i}> 
                                    <Field name={`asignatura[${i}]`} />
                                    <BotonFormulario value="Borrar" func={() => remove()}/>
                                </div>
                            ))
                        }
                        </div>
                    )
                    }
                } 
            </FieldArray>
            <BotonFormulario value="Agregar" func={props.func}/>
        </div>
    );
}

export default FormularioAsignaturas;
