import React from 'react';
import asignaturas from './Asignaturas.json'

const OpcionAsignatura = () => {
    const ramo = asignaturas.filter(ramo => ramo.ramo === props.name).map((ramo) => 
        <option key={ramo.id} value={ramo.ramo}>
            {ramo.ramo}
        </option>
    );
    return(
        <>{ramo}</>
    );
}

const OptGroupAsignatura = (props) => {
    const ramo = asignaturas.filter(ramo => ramo.materia === props.name).map((ramo) => 
        <optgroup key={ramo.id} label={ramo.materia}>
            <OpcionAsignatura />
        </optgroup>
    );
    return(
        <>{ramo}</>
    );
}

const SelectAsignaturas = (props) => {
    return(
        <select name={props.name}>
            <OptGroupAsignatura name="MatemÃ¡ticas"/>
        </select>
    );
}

export default SelectAsignaturas;
