import React, { useRef, useState } from 'react';
import { Formik, Form, Field, ErrorMessage, useField } from 'formik';

const Radio = ({label, ...props}) => {
    const [field] = useField({ ...props, type: 'radio'});
    return(
        <div>
            <label>
                <input type='radio' {...field} {...props}/>
                {label}
            </label>
        </div>
    );
}

const Button = (props) => {
    let button = '';
    if(props.estado < 3){
        button = <button type="submit" >Siguiente</button>
    }
    else{
        button = <button type="submit">Crear cuenta</button>
    }
    return(
        <div>{button}</div>
    );
}

const Formularios = (props) => {
    let formulario = '';
    if(props.estado === 0){
        formulario = 
            <Formik 
                initialValues={{tipo:''}}
                validate={validaciones}
                onSubmit={(values =>(values) => props.updateTipo(values))}
            >
                <Form>
                    <Radio name="tipo" value="estudiante" label="Estudiante" />
                    <br/>
                    <Radio name="tipo" value="tutor" label="Tutor" />
                    <br/>
                    <ErrorMessage name="tipo" />
                    <Button name="boton" estado={props.estado} update={props.updateEstado}/>
                </Form>
            </Formik>
    }
    else if(props.estado === 1){
        formulario = 
            <Formik 
                initialValues={{tipo:''}}
                validate={validaciones}
                onSubmit={(values => console.log(values))}
            >
                <Form>
                    <Radio name="tipo" value="tutor" label="Tutor" />
                    <br/>
                    <Button name="boton" estado={props.estado} update={props.updateEstado}/>
                </Form>
            </Formik>
    }
    return (
        <div>{formulario}</div>
    );
}

const validaciones = (values) => {
    const errors = {};

    if(!values.tipo){
        errors.tipo = 'Debe asignar un valor'
    }

    return errors;
}


const SignUp = () => {
    const [estado, setEstado] = useState(0);
    // updateEstado -> avanza en el formulario
    // estado = 0 -> tutor o estudiante
    // estado = 1 -> datos personales 
    // estado = 2 -> asignaturas
    // estado = 3 -> horario
    const updateEstado = () => {
        setEstado(estado+1);
    }

    const [tipo, setTipo] = useState('');
    const updateTipo = (nuevo_tipo) => {
        setTipo(nuevo_tipo);
    }


    return(
        <div>
            <h1>Página crear cuenta</h1>
                <Formularios estado={estado} updateEstado={updateEstado} updateTipo={updateTipo}/>
        </div>
    );
}

export default SignUp
