import {useState} from 'react';

import './PagoEnLinea.css';

const imageUrls = [
  "https://logos-world.net/wp-content/uploads/2020/04/Visa-Logo.png",
  "https://brand.mastercard.com/content/dam/mccom/brandcenter/thumbnails/mastercard_vrt_rev_92px_2x.png",
  "https://www.discover.com/company/images/newsroom/media-downloads/discover.png",
  "https://s1.q4cdn.com/692158879/files/design/svg/american-express-logo.svg",
  "https://cdn4.iconfinder.com/data/icons/simple-peyment-methods/512/diners_club-512.png",
  "https://upload.wikimedia.org/wikipedia/commons/thumb/4/40/JCB_logo.svg/1280px-JCB_logo.svg.png"
]

const PagoEnLinea = () => {
  const [creditCardNum, setCreditCardNum] = useState('');
  const [cardType, setCardType] = useState('s')
  const [cardHolder, setCardHolder] = useState('');
  const [expireMonth, setExpireMonth] = useState('MM');
  const [expireYear, setExpireYear] = useState('YYYY');
  const [cardTypeUrl, setCardTypeUrl] = useState('https://brand.mastercard.com/content/dam/mccom/brandcenter/thumbnails/mastercard_vrt_rev_92px_2x.png');
  // const [flip, setFlip] = useState(null);

  const handleNum = (e) => {
    setCreditCardNum(e.target.rawValue);
  }

  const handleType = (type) => {
    setCardType(type);
    console.log(type);

    if(type === "visa") {
      setCardTypeUrl(imageUrls[0]);
      console.log("Visa")
    } else if(type === "mastercard") {
      setCardTypeUrl(imageUrls[1]);
      console.log("Mastercard")
    } else if(type === "discover") {
      setCardTypeUrl(imageUrls[2]);
      console.log("Discover")
    } else if(type === "amex") {
      setCardTypeUrl(imageUrls[3]);
      console.log("Amex")
    } else if(type === "diners") {
      console.log("Diners")
      setCardTypeUrl(imageUrls[4])
    } else if(type === "jcb") {
      console.log("JCB");
      setCardTypeUrl(imageUrls[5]);
    }
  }

  const handleCardHolder = (e) => {
    setCardHolder(e.target.value);
  }

  const handleExpMonth = (e) => {
    setExpireMonth(e.target.value);
  }

  const handleExpYear = (e) => {
    setExpireYear(e.target.value);
  }

  return (
       <div className="container">
        <form id="form">
            <div id="card">
                <div className="header">
                    <div className="sticker"></div>
                    <div>
                      <img className="logo" src={cardTypeUrl} alt="Card logo"/>
                    </div>
                </div>
                <div className="body">
                    <h2 id="creditCardNumber">{creditCardNum}</h2>
                </div>
                <div className="footer">
                    <div>
                        <h5>Titular</h5>
                        <h3>{cardHolder}</h3>
                    </div>
                    <div>
                        <h5>Expiración</h5>
                        <h3>{expireMonth} / {expireYear}</h3>
                    </div>
                </div>
            </div>

            <div className="input-container mt">
                <h4>Ingrese su tarjeta paga efectuar el pago</h4>
                <input onChange={handleNum}
                  placeholder="Número de la tarjeta"/>
            </div>

            <div className="input-container">
                <h4>Titular de la tarjeta</h4>
                <input onChange={handleCardHolder} type="text" placeholder="Nombre completo" required/>
            </div>

            <div className="input-grp">
                <div className="input-container">
                    <h4>Fecha de expiración</h4>
                    <select value={expireYear} onChange={handleExpYear}>
                      <option value="January">Enero</option>
                      <option value="February">Febrero</option>
                      <option value="March">Marzo</option>
                      <option value="April">Abril</option>
                      <option value="May">Mayo</option>
                      <option value="June">Junio</option>
                      <option value="July">Julio</option>
                      <option value="August">Agosto</option>
                      <option value="September">September</option>
                      <option value="October">Octubre</option>
                      <option value="November">Noviembre</option>
                      <option value="December">Diciembre</option>
                    </select>
                </div>
                <div className="input-container">
                <h4>Mes</h4>
                <select value={expireMonth} onChange={handleExpMonth}>
                      <option value="2021">2021</option>
                      <option value="2022">2022</option>
                      <option value="2023">2023</option>
                      <option value="2024">2024</option>
                      <option value="2025">2025</option>
                      <option value="2026">2026</option>
                      <option value="2027">2027</option>
                      <option value="2028">2028</option>
                      <option value="2029">2029</option>
                    </select>
                </div>
                <div className="input-container">
                    <h4>CVV</h4>
                    <input type="password" placeholder="CVV" required/>
                </div>
            </div>
            <div className="input-grp2">
                <h2>Monto total:</h2>
            </div>

            <button>Pagar</button>
        </form>
    </div>
  );
}

export default PagoEnLinea;
