import React, { useState } from 'react';
import './Horario.css';

const horas = [
    '8:00 AM',
    '9:00 AM',
    '10:00 AM',
    '11:00 AM',
    '12:00 PM',
    '13:00 PM',
    '14:00 PM',
    '15:00 PM',
    '16:00 PM',
    '17:00 PM',
    '18:00 PM',
    '19:00 PM',
    '20:00 PM',
    '21:00 PM',
    '22:00 PM',
    '23:00 PM'
] 

const dias = ['Horario', 'Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];

const Bloque = (props) => {
    let bloque = '';
        {
            (props.dia === "horario") ?
            bloque = <div hora={props.hora}>{props.hora}</div>
            :
            bloque = <div className={props.className} hora={props.hora} dia={props.dia}>Libre</div>
        }
    return(
        <>{bloque}</>
    );
}

const Hora = (props) => {
    let bloque = [];
    for(let i = 1; i < horas.length; i++){
        {
            (props.dia === "horario") ?
            bloque.push(<Bloque hora={horas[i]} dia={props.dia} key={i}/>)
            :
            bloque.push(<Bloque className={props.className} hora={horas[i]} dia={props.dia} key={i}/>)
        }
    }
    return(
        <>{bloque}</>
    );
}

const Dia = () => {
    const[seleccion, setSeleccion] = useState(false);
    const seleccionar = (estado) => {
        if(estado){
            setSeleccion(true);
        }
        else{
            setSeleccion(false);
        }
    };

    let horario = dias.map((dias) => 
        <div className='grid-dias'>
                {dias}
                {
                    (dias === 'Horario') ? 
                    <Hora dia="horario" />
                    : 
                        <Hora className='grid-bloque' dia={dias}/> 
                }
        </div>
        )

    return(
        <div className='grid-horario'>
            {horario}
        </div>
    );
}

const Horario = () => {
    return(
        <Dia />
    );
}

export default Horario;





































