import './Calificacion.css'
import { Form, Formik ,Field } from 'formik'
import BotonFormulario from '../botones/BotonFormulario'
import axios from 'axios'
import { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'

const api = axios.create({
    baseURL: `http://localhost:2000/api/events`
})

const Calificacion = () =>{
    const tutor = useLocation().pathname.split("/")[2]
    const [nombreTutor, setnombreTutor] = useState('')
    const [puntuacion, setPuntuacion] = useState('')
    useEffect(() => {
        const mostrarCalificacion = async() => {
            try{
                const response = await api.get("/obtenerCalificacion/"+tutor)
                if(response){
                    setnombreTutor(response.data.nombre_tutor)
                    setPuntuacion(response.data.calificacion)
                }
            }catch(error){
                console.log(error)
            }
        }
        mostrarCalificacion()
    })

    const calcularPromedio = () => {

