import './ChatVirtual.css'
import { Formik, Form, Field, ErrorMessage, isObject } from 'formik';
import BotonFormulario from '../botones/BotonFormulario';
import io from 'socket.io-client'
import ObtenerUsuarioID from '../controllers/ObtenerUsuarioID';
import { useEffect } from 'react';import axios from 'axios'

const api_checkTOKEN = axios.create({
  withCredentials: true, 
  credentials: 'include',
  baseURL: `http://localhost:2000/api/checkToken`
})



const socket = io('http://localhost:2000/')
socket.on("connection")

const ChatVirtual = (props) => {
    const [usuarioID, setUsuarioID] = useState();
    useEffect(() => {
        const obtenerID = async () => {
            try{
                //obtener ID desde el token
                const usuario = await api_checkTOKEN.get('/checkUser',{
                    headers: {
                    'Content-type': 'application/json',
                    'x-token': mi_token
                    }
                });
                setUsuarioID(usuario.data.usuario.id)
            }catch(error){
                console.log(error)
            }
        }
        obtenerID();
    }, [])
    const enviarMensaje = (mensaje) => {
        console.log(mensaje)
        socket.emit("mensaje", mensaje)
    }
