import { useEffect, useState } from 'react';
import axios from 'axios'

const ZoomAPI = axios.create({
  withCredentials: true, 
  credentials: 'include',
  baseURL: `http://localhost:2000/api/zoom`
})

const APIZoom = () => {
    const [enlace_reunion, setEnlace] = useState("")
    const [validarEnlace, setValidacion] = useState(false)

    useEffect(() => {
        const generarEnlace = async () => {
        try{
                const mi_token = localStorage.getItem('x-token')
                const response = ZoomAPI.get("/ZoomMeet", {
                    headers: {
                        'Content-type': 'application/json',
                        'x-token': mi_token
                    }
                })
                if(response.data){
                    if(!validarEnlace){
                        console.log(response.data)
                        setEnlace(response.data.enlaceZoom)
                        setValidacion(true)
                    }
                }
        }catch(error){
            console.log(error)
        }
        }
        generarEnlace();
    }, [enlace_reunion])

    return(
        <>
        {
            console.log(enlace_reunion)
            /*
            enlace_reunion !== "" &&
            <a href={enlace_reunion}>{"Su enlace es: "+enlace_reunion}</a>
            */
        }
        </>       
    )

}

export default APIZoom
