import React from 'react';
import './Horario.css';

const horas = 16;
const dias = ['Lunes', 'Martes', 'Miercoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];

const Bloque = (props) => {
    return(
        <div hora={props.hora} dia={props.dia}>{props.hora}</div>
    );
}

const Hora = (props) => {
    let bloque = [];
    for(let i = 1; i <= horas; i++){
        bloque.push(<Bloque hora={i} dia={props.dia} key={i}/>)
    }
    return(
        <div>{bloque}</div>
    );

}

const Dia = () => {
    let dia = []
    let horas = dias.map((dias) => <Hora dia={dias} key={dias}/> )
    return(
    <div>
        <div className='grid-container'>
            {horas}
        </div>
    </div>
    );

}

const Horario = () => {
    return(
        <Dia />
    );
}

export default Horario;





































